import { Component, Inject } from '@angular/core';
import { UserService } from 'src/app/Services/user.service';
import { USER_TOKEN } from '../../../app.module'

@Component({
  selector: 'app-user-list',
  templateUrl: './user-list.component.html',
})
export class UserListComponent {

  constructor(@Inject(USER_TOKEN) private userService: UserService){
  }

  userList = this.userService.GetAllUsers();

  ShowUserDetails(user){
    this.userService.OnShowUserdetails
  }

}
<div class="admin-user-list">
    <div class="admin-user-detail" *ngFor="let user of userList">
        <div class="admin-user-name">{{user.name}}</div>
        <div class="admin-user-gender">{{user.gender}}</div>
        <div class="admin-user-subtype">{{user.subType}}</div>
        <div class="admin-user-active">{{user.status}}</div>
        <button class="admin-user-show-detail-button" (click)="ShowUserDetails(user)">+</button>
    </div>   
</div>
