import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html'
})
export class HeaderComponent {
  selectedTab: string = 'home';

  //When HOME Link is clicked
  HomeClicked(){
    this.selectedTab = 'home';
  }

  //When Admin Link is clicked
  AdminClicked(){
    this.selectedTab = 'admin';
  }

  OnSubscribe(){
    //ADD USER TO DATABASE

  //SEND EMAIL WITH SUBSCRIPTION DETAIL 

  //ALLOW USER TO ACCESS THE SERVICES

  alert('Thank you for subscribing. You can the service now.')
  }
}
<div class="header-container">
    <div class="header-logo">MYSITE</div>
    <div class="header-nav">
        <a href="#" (click)="HomeClicked()">HOME</a>
        <a href="#" (click)="AdminClicked()">ADMIN</a>
        <button class="header-button" (click)="OnSubscribe()">SUBSCRIBE</button>
    </div>
</div>
<div [ngSwitch]="selectedTab">
    <app-home *ngSwitchCase="'home'"></app-home>
    <app-admin *ngSwitchCase="'admin'"></app-admin>
</div>
