import { Component, Inject } from '@angular/core';
import { UserService } from 'src/app/Services/user.service';

@Component({
  selector: 'app-user-list',
  templateUrl: './user-list.component.html',
  providers: [UserService]
})
export class UserListComponent {

  constructor(private userService: UserService){
  }

  userList = this.userService.GetAllUsers();

}
<div class="admin-user-list">
    <div class="admin-user-detail" *ngFor="">
        <div class="admin-user-name">John Smith</div>
        <div class="admin-user-gender">Male</div>
        <div class="admin-user-subtype">Yearly</div>
        <div class="admin-user-active">Active</div>
        <button class="admin-user-show-detail-button">+</button>
    </div>   
</div>
